import React, { useState } from 'react';
import './App.css';
import Footer from './components/Footer';  // Importando o Footer
import VideoList from './components/VideoList';
import VideoForm from './components/VideoForm';

function App() {
  const [videos, setVideos] = useState([
    { id: 1, title: 'Vídeo 1', description: 'Descrição do vídeo 1' },
    { id: 2, title: 'Vídeo 2', description: 'Descrição do vídeo 2' },
  ]);
  const [videoToEdit, setVideoToEdit] = useState(null);

  const handleAddVideo = (newVideo) => {
    setVideos([...videos, { id: Date.now(), ...newVideo }]);  // Adiciona um novo vídeo
  };

  const handleEditVideo = (video) => {
    setVideoToEdit(video);  // Define o vídeo a ser editado
  };

  const handleUpdateVideo = (updatedVideo) => {
    // Atualiza o vídeo na lista
    setVideos(
      videos.map((video) =>
        video.id === updatedVideo.id ? updatedVideo : video // Substitui o vídeo com id correspondente
      )
    );
    setVideoToEdit(null);  // Limpa o vídeo em edição após atualização
  };

  const handleDeleteVideo = (id) => {
    setVideos(videos.filter((video) => video.id !== id));  // Remove o vídeo da lista
  };

  return (
    <div className="App">
      <h1>Gerenciador de Vídeos</h1>
      <VideoForm
        onSubmit={videoToEdit ? handleUpdateVideo : handleAddVideo} // Se está editando, chama handleUpdateVideo, senão chama handleAddVideo
        videoToEdit={videoToEdit} // Passa o vídeo para edição, se houver
      />
      <VideoList
        videos={videos}
        onEdit={handleEditVideo}  // Passa a função para editar
        onDelete={handleDeleteVideo}  // Passa a função para excluir
      />
    </div>
  );
}

export default App;
