import React from 'react';

function VideoItem({ video, onEdit, onDelete }) {
  return (
    <li>
      <h3>{video.title}</h3>
      <p>{video.description}</p>
      <button onClick={() => onEdit(video)}>Editar</button>
      <button onClick={() => onDelete(video.id)}>Excluir</button>
    </li>
  );
}

export default VideoItem;
