import React, { useState, useEffect } from "react";
import AddVideoForm from "./components/AddVideoForm";
import VideoList from "./components/VideoList";

const App = () => {
  // Estado para armazenar os vídeos
  const [videos, setVideos] = useState([]);

  // Carrega os vídeos do localStorage ao inicializar
  useEffect(() => {
    const storedVideos = JSON.parse(localStorage.getItem("videos")) || [];
    setVideos(storedVideos);
  }, []);

  // Atualiza o localStorage sempre que os vídeos mudam
  useEffect(() => {
    localStorage.setItem("videos", JSON.stringify(videos));
  }, [videos]);

  // Função para adicionar vídeo
  const addVideo = (newVideo) => {
    setVideos((prevVideos) => [...prevVideos, newVideo]);
  };

  // Função para excluir vídeo
  const deleteVideo = (id) => {
    setVideos((prevVideos) => prevVideos.filter((video) => video.id !== id));
  };

  // Função para atualizar um vídeo
  const updateVideo = (updatedVideo) => {
    setVideos((prevVideos) =>
      prevVideos.map((video) =>
        video.id === updatedVideo.id ? updatedVideo : video
      )
    );
  };

  return (
    <div>
      <h1>Plataforma de Vídeos</h1>
      <AddVideoForm addVideo={addVideo} />
      <VideoList
        videos={videos}
        deleteVideo={deleteVideo}
        updateVideo={updateVideo}
      />
    </div>
  );
};

export default App;
