import React, { useState, useEffect } from 'react';
import VideoList from './components/VideoList';
import VideoForm from './components/VideoForm';
import { getVideos, addVideo, updateVideo, deleteVideo } from './services/videoService';

function App() {
  const [videos, setVideos] = useState([]);
  const [videoToEdit, setVideoToEdit] = useState(null);

  useEffect(() => {
    const fetchVideos = async () => {
      const data = await getVideos();
      setVideos(data);
    };
    fetchVideos();
  }, []);

  const handleAddVideo = async (newVideo) => {
    const addedVideo = await addVideo(newVideo);
    setVideos([...videos, addedVideo]);
  };

  const handleUpdateVideo = async (updatedVideo) => {
    await updateVideo(updatedVideo);
    setVideos(videos.map(video => video.id === updatedVideo.id ? updatedVideo : video));
    setVideoToEdit(null);
  };

  const handleDeleteVideo = async (id) => {
    await deleteVideo(id);
    setVideos(videos.filter(video => video.id !== id));
  };

  return (
    <div className="App">
      <h1>Gerenciador de VÃ­deos</h1>
      <VideoForm onSubmit={videoToEdit ? handleUpdateVideo : handleAddVideo} videoToEdit={videoToEdit} />
      <VideoList 
        videos={videos} 
        onEdit={setVideoToEdit} 
        onDelete={handleDeleteVideo} 
      />
    </div>
  );
}

export default App;
