import React, { useState } from 'react';
import './App.css';

import VideoList from './components/VideoList';
import VideoForm from './components/VideoForm';

function App() {
  const [videos, setVideos] = useState([
    { id: 1, title: 'Vídeo 1', description: 'Descrição do vídeo 1' },
    { id: 2, title: 'Vídeo 2', description: 'Descrição do vídeo 2' },
  ]);
  const [videoToEdit, setVideoToEdit] = useState(null);

  const handleAddVideo = (newVideo) => {
    setVideos([...videos, { id: Date.now(), ...newVideo }]);
  };

  const handleEditVideo = (video) => {
    setVideoToEdit(video);
  };

  const handleUpdateVideo = (updatedVideo) => {
    setVideos(
      videos.map((video) =>
        video.id === updatedVideo.id ? { ...video, ...updatedVideo } : video
      )
    );
    setVideoToEdit(null); // Limpa o vídeo em edição após a atualização
  };

  const handleDeleteVideo = (id) => {
    setVideos(videos.filter((video) => video.id !== id));
  };

  return (
    <div className="App">
      <h1>Gerenciador de Vídeos</h1>

      <VideoForm
        onSubmit={videoToEdit ? handleUpdateVideo : handleAddVideo}
        videoToEdit={videoToEdit}
      />

      <VideoList
        videos={videos}
        onEdit={handleEditVideo}
        onDelete={handleDeleteVideo}
      />
    </div>
  );
}

export default App;
